content:
{
 "policyType": "BuiltIn",
 "mode": "Indexed",
 "metadata": {
 "policy_id": "/providers/Microsoft.Authorization/policyDefinitions/399b2637-a50f-4f95-96f8-3a145476eb15",
 "policy_description": "Enable FTPS enforcement for enhanced security",
 "classification": "Security",
 "sub_classification": "Application",
 "service_resource": [
 "Web_Apps"
 ],
 "resource_type": "App_Services",
 "severity": "medium",
 "uri": "policy/azure_policy/global/azure/compliance/others/uIDkY"
 },
 "parameters": {
 "effect": {
 "type": "String",
 "allowedValues": [
 "AuditIfNotExists",
 "Disabled"
 ],
 "defaultValue": "AuditIfNotExists",
 "metadata": {
 "displayName": "Effect",
 "description": "Enable or disable the execution of the policy"
 }
 }
 },
 "policyRule": {
 "if": {
 "allOf": [
 {
 "field": "type",
 "equals": "Microsoft.Web/sites"
 },
 {
 "field": "kind",
 "like": "functionapp*"
 }
 ]
 },
 "then": {
 "effect": "[parameters('effect')]",
 "details": {
 "type": "Microsoft.Web/sites/config",
 "name": "web",
 "existenceCondition": {
 "field": "Microsoft.Web/sites/config/ftpsState",
 "equals": "FtpsOnly"
 }
 }
 }
 }
}
