content:
{
 "policyType": "BuiltIn",
 "mode": "Indexed",
 "metadata": {
 "policy_id": "/providers/Microsoft.Authorization/policyDefinitions/aa81768c-cb87-4ce2-bfaa-00baa10d760c",
 "policy_description": "Managed service identity in App Service makes the app more secure by eliminating secrets from the app, such as credentials in the connection strings. When registering with Azure Active Directory in the app service, the app will connect to other Azure services securely without the need of username and passwords",
 "classification": "Security",
 "sub_classification": "Application",
 "service_resource": [
 "Web_Apps"
 ],
 "resource_type": "App_Services",
 "severity": "high",
 "uri": "policy/azure_policy/global/azure/compliance/others/fftnu"
 },
 "parameters": {
 "effect": {
 "type": "String",
 "allowedValues": [
 "AuditIfNotExists",
 "Disabled"
 ],
 "defaultValue": "AuditIfNotExists",
 "metadata": {
 "displayName": "Effect",
 "description": "Enable or disable the execution of the policy"
 }
 }
 },
 "policyRule": {
 "if": {
 "allOf": [
 {
 "field": "type",
 "equals": "Microsoft.Web/sites"
 },
 {
 "field": "kind",
 "like": "app*"
 }
 ]
 },
 "then": {
 "effect": "[parameters('effect')]",
 "details": {
 "type": "Microsoft.Web/sites/config",
 "name": "web",
 "existenceCondition": {
 "field": "Microsoft.Web/sites/config/web.managedServiceIdentityId",
 "exists": "true"
 }
 }
 }
 }
}
